<template>
	<div>
		<div id="graph" ref="container"></div>
		<div className="graph-io">
			<span title="导出画图数据" @click="exportJson">
				<img
					src="https://cdn.jsdelivr.net/gh/Logic-Flow/static@latest/docs/examples/case/bussiness/bpmn/download.png"
					alt="导出画图数据"
				/>
			</span>
			<span id="upload-xml" title="导入画图数据">
				<input type="file" className="upload" @change="uploadJson" />
				<img
					className="upload-img"
					src="https://cdn.jsdelivr.net/gh/Logic-Flow/static@latest/docs/examples/case/bussiness/bpmn/upload.png"
					alt="导入画图数据"
				/>
			</span>
		</div>
	</div>
</template>
<script setup>
import { ref, onMounted } from "vue";
// 导入LF样式表和库
import "@logicflow/extension/lib/style/index.css";
import "@logicflow/core/lib/style/index.css";
import LogicFlow from "@logicflow/core";
import { BpmnElement, BpmnXmlAdapter, Control, Menu, SelectionSelect, DndPanel } from "@logicflow/extension";

// 声明容器的对应ref对象和LF对象
const container = ref();
const lf = ref();

onMounted(() => {
	// 创建LF实例
	lf.value = new LogicFlow({
		// 指定渲染的容器
		container: container.value,
		// 显示网格
		grid: true,
		// 禁止缩放画布
		stopZoomGraph: true,
		metaKeyMultipleSelected: true,
		keyboard: {
			enabled: true,
		},
		snapline: true,
		plugins: [BpmnElement, BpmnXmlAdapter, Control, Menu, SelectionSelect, DndPanel],
	});
	// 设置bpmn功能按钮
	lf.value.setPatternItems([
		{
			label: "选区",
			icon: "data:image/png;base64,",
			callback: () => {
				lf.value.openSelectionSelect();
				lf.value.once("selection:selected", () => {
					lf.value.closeSelectionSelect();
				});
			},
		},
		{
			type: "bpmn:startEvent",
			label: "开始",
			text: "开始",
			icon: "data:image/png;base64,",
		},
		{
			type: "bpmn:userTask",
			label: "用户任务",
			text: "用户任务",
			icon: "data:image/png;base64,",
		},
		{
			type: "bpmn:serviceTask",
			label: "系统任务",
			text: "系统任务",
			icon: "data:image/png;base64,",
		},
		{
			type: "bpmn:exclusiveGateway",
			label: "条件判断",
			text: "条件判断",
			icon: "data:image/png;base64,",
		},
		{
			type: "bpmn:endEvent",
			label: "结束",
			text: "结束",
			icon: "data:image/png;base64,",
		},
	]);
	// 绑定节点点击事件
	lf.value.on("node:click", ({ data }) => {
		console.log("node:click", data);
	});
	// 渲染数据
	lf.value.render({});
});

const download = (filename, text) => {
	const element = document.createElement("a");
	element.setAttribute("href", "data:text/plain;charset=utf-8," + encodeURIComponent(text));
	element.setAttribute("download", filename);
	element.style.display = "none";
	document.body.appendChild(element);
	element.click();
	document.body.removeChild(element);
};

// 导出json数据
const exportJson = () => {
	const data = JSON.stringify(lf.value.getGraphRawData());
	download("logic-flow.json", data);
};

// 导入json数据
const uploadJson = (ev) => {
	const file = ev.target.files[0];
	const reader = new FileReader();
	reader.onload = (event) => {
		if (event.target) {
			const data = JSON.parse(event.target.result);
			lf.value.renderRawData(data);
		}
	};
	reader.readAsText(file);
};
</script>

