	box-shadow: 0 1px 4px rgba(0, 0, 0, 0.3);
	padding: 10px;
	display: flex;
}
.graph-io > span {
	margin: 0 5px;
	cursor: pointer;
}
#upload-xml {
	position: relative;
	overflow: hidden;
	display: inline-block;
	cursor: pointer;
}
.upload {
	position: absolute;
	z-index: 99;
	left: 0;
	top: 0;
	opacity: 0;
	cursor: pointer;
}
.upload::-webkit-file-upload-button {
	cursor: pointer;
}
.upload-img {
	width: 26px;
}
</style>
